document.addEventListener("DOMContentLoaded",()=>{function t(n){const r=1e3*parseFloat(n.getAttribute("data-float-period")),a=performance.now();requestAnimationFrame(function e(){var t=(performance.now()-a)%r/r*2*Math.PI,t=-5*Math.sin(t);n.style.transform=`translateY(${t}px)`,requestAnimationFrame(e)})}document.addEventListener("click",e=>console.log(e.target)),document.querySelector(".first-screen__bg-images").querySelectorAll(".layer").forEach(e=>{t(e)})});